const ProductsList = ({products}) => {

       /* const handleDelete = (codigo) => {
            const requestInit = {
                method: 'DELETE',
            }

            fetch('http://localhost:4000/products/${codigo}', requestInit)
            .then(res => res.text())
            .then(res => console.log(res))
        }*/

       removeProducts = (codigo) => {

            fetch (`http://localhost:4000/products/${codigo}`, {
              method: 'DELETE'
            })
            .then(res => res.json())
            .then(res => {
              if (res.success) {
                let products = this.state.products.filter(p => p.codigo !== codigo);
                this.setState({ products });
                alert('Categoría eliminada');
              }
            });
          }

    return (
        <table className="table">
            <thead>
                <tr>
                    <th>CODIGO</th>
                    <th>Nombre del Producto</th>
                    <th>PRECIO</th>
                    <th>FABRICADO</th>
                    <th>FECHA VENCIMIENTO</th>
                    <th>ELIMINAR</th>
                </tr>
            </thead>
            <tbody>
                {products.map(products => ( 
                 <tr key={products.codigo}>
                     <td>{products.codigo}</td>
                     <td>{products.name}</td>
                     <td>{products.precio}</td>
                     <td>{products.fabricado}</td>
                     <td>{products.fecha_vencimiento}</td>
                     <td>
                        <div className="mb-3">
                        <button onClick={this.removeProducts.bind(this, products.codigo)}>Eliminar</button>

                        </div>
                     </td>
                 </tr>
                ))}
            </tbody>
        </table>
    );
}
export default ProductsList;
