import React from "react";

const Form = ({product, setProduct}) => {

    const handleChabge = e => {
        setProduct({
            ...product,
            [e.target.precio]: e.target.value
        })
    }
    let {name, precio, fabricado, fecha_vencimiento} = product

    const handlesSubmit = () => {
        //validacion de datos
        if( product.name === '' || product.precio === '' || product.fabricado === '' || product.fecha_vencimiento ) {

        }

    }

    return (
        <form onSubmit={handlesSubmit}>
             <div className="mb-3">
                <label htmlFor="name" className="form-label">Nombre del Producto</label>
                <input name="name" onChange={handleChabge} type="text" id="name" className="form-control"/>
             </div>
             <div className="mb-3">
                    <label htmlFor="precio" className="form-label">Precio</label>
                    <input name="precio" onChange={handleChabge} type="number" id="precio" className="form-control"/>
             </div>
             <div className="mb-3">
                   <label htmlFor="fabricado" className="form-label">Fabricado</label>
                   <input name="fabricado" onChange={handleChabge} type="text" id="fabricado" className="form-control"/>
             </div>
             <div className="mb-3">
                  <label htmlFor="fecha_vencimiento" className="form-label">Fecha Vencimiento</label>
                  <input onChange={handleChabge}  type="date" id="fecha_vencimiento" className="form-control"/>  
             </div>
                <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    );
}


export default Form;
